import React, { useReducer } from "react";
import CartContext from "./CartContext";

const defaultState = {
  item: [],
  totalAmount: 0,
};
const cartReducer = (state, action) => {
  if (action.type === "ADD") {
    //const updatedItem = state.item.concat(action.item);

    const existingItemIndex = state.item.findIndex(
      (item) => item.id === action.item.id
    );

    const existingCartItem = state.item[existingItemIndex];

    let updatedItems;
    if (existingCartItem) {
      const updatedItem = {
        ...existingCartItem,
        amount: state.amount + action.item.amount,
      };
      updatedItems = { ...state.item };
    }
    const updatedTotalAmount =
      +state.totalAmount + action.item.amount * action.item.price;

    return { item: updatedItem, totalAmount: +updatedTotalAmount };
  }
  return defaultState;
};
const CartContextProvider = (props) => {
  const [cartState, dispatchCartAction] = useReducer(cartReducer, defaultState);

  const addItemHandler = (item) => {
    dispatchCartAction({ type: "ADD", item: item });
  };
  const removeItemHandler = (id) => {
    dispatchCartAction({ type: "REMOVE", value: id });
  };

  const cartContext = {
    items: cartState.item,
    totalAmount: cartState.totalAmount,
    addItem: addItemHandler,
    removeItem: removeItemHandler,
  };
  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartContextProvider;
